<?php/** * Raindrop Framework for PHP * * WeChat Model of WeChat Pay Unified Order * * @author Luuray * @copyright Rainhan System * @id $Id$ * * Copyright (c) 2010-2017, Rainhan System * Site: raindrop-php.rainhan.net */namespace Raindrop\Component\WeChat\Model;use Raindrop\Exceptions\NotImplementedException;class UnifiedOrder implements \JsonSerializable, \Serializable{	protected $_aData;	public function __construct($aData)	{		$this->_aData = $aData;	}	public function __set($k, $v)	{		throw new NotImplementedException();	}	public function __get($sKey)	{		if (array_key_exists(strtolower($sKey), $this->_aData)) {			return $this->_aData[strtolower($sKey)];		} else if (array_key_exists(str_uc2underscore($sKey), $this->_aData)) {			return $this->_aData[str_uc2underscore($sKey)];		} else if (method_exists($this, "get{$sKey}")) {			$sMethod = "get{$sKey}";			return $this->$sMethod();		} else {			return null;		}	}	public function getOrderNumber()	{		return $this->out_trade_no;	}	public function getAmount()	{		return $this->total_fee * 0.01;	}	public function jsonSerialize()	{		return json_encode($this->_aData);	}	public function serialize()	{		return serialize($this->_aData);	}	public function unserialize($serialized)	{		$this->_aData = $this->unserialize($serialized);	}}